mapper.update:
update shipping_address sa, register rs set sa.addr_zip_code = #{addr_zip_code}, sa.addr_name = #{addr_name}, sa.addr_detail = #{addr_detail}, sa.addr_tel = #{addr_tel}, sa.addr_emergentel = #{addr_emergentel}, sa.addr_require = #{addr_require}, sa.addr_def = #{addr_def}
,rs.user_address = #{addr_zip_code}, rs.user_detailaddress = #{addr_detail}
where rs.user_code = #{user_code} and sa.addr_name = #{addr_name}
