mapper.select:
SELECT om.order_code, rs.user_code, rs.user_name, rs.user_phone, rs.user_detailaddress, om.payment_method, rs.user_bank,
pt.prd_code, pt.prd_name, pt.prd_regular_price, pt.prd_discount_price, pt.prd_discount_percentage, pt.prd_main_image, pt.prd_brif_explain,
om.payment_status, om.order_status, om.cs_status, om.payment_amount, om.create_date from order_mst om LEFT OUTER JOIN product pt ON(pt.prd_code = om.prd_code) LEFT OUTER JOIN register rs ON(rs.user_code = om.user_code) WHERE om.order_code = #{orderCode};

mapper.update:
update
	cart
set
	cart_product_count = #{productCount}
where
	cart_id = #{cartId}
